import { FETCH_POSTS_LIST, successPostsList } from "../../actions/postsList";
import { getPostsListAPI } from "../../../api/getPostsListAPI";

import { call, put, takeLatest, all, fork } from "redux-saga/effects";

import postsListSaga from "./postsListSaga";

export default function* rootSaga() {
  yield all([takeLatest(FETCH_POSTS_LIST, postsListSaga)]);
}

function* workerSaga() {
  try {
    const data = yield call(getPostsListAPI);
    yield put(successPostsList(data));
  } catch (e) {}
}
