import React from "react";

const Textarea = ({ name, handleControl, register, isFocus }) => (
  <textarea
    className="textarea txt--18"
    rows={10}
    name={name}
    onFocus={handleControl}
    onBlur={handleControl}
    ref={register}
    placeholder={`${!isFocus ? "| Type Your message" : ""} `}
  />
);

export default Textarea;
