import {
  FETCH_POSTS_LIST,
  SUCCESS_POSTS_LIST,
  ERROR_POSTS_LIST,
} from "../actions/postsList";

import { HYDRATE } from "next-redux-wrapper";

type InitialStateType = {
  data: Array<Object> | null;
  error: boolean;
};

type ActionPayloadType = {
  postsList?: Array<Object>;
};

type ActionType = {
  type:
    | typeof SUCCESS_POSTS_LIST
    | typeof ERROR_POSTS_LIST
    | typeof FETCH_POSTS_LIST
    | typeof HYDRATE;
  payload?: ActionPayloadType;
};

const initialState: InitialStateType = {
  data: null,
  error: false,
};

const postsListReducer = (
  state = initialState,
  action: ActionType
): InitialStateType => {
  switch (action.type) {
    case "__NEXT_REDUX_WRAPPER_HYDRATE__": {
      return { ...state, ...action.payload };
    }
    case SUCCESS_POSTS_LIST:
      return { ...state, data: action.payload.postsList };
    case ERROR_POSTS_LIST:
      return { ...state, error: true };
    default:
      return { ...state };
  }
};

export default postsListReducer;
