import React, { useState, useCallback } from "react";

import { FieldContainer, Label, FieldContent } from "./styledComponent";

const Field = ({
  name,
  label,
  placeholder,
  errors,
  register,
  component: Component,
  validator,
}) => {
  const error = errors[name];
  const [isFocus, setIsFocus] = useState(false);

  const handleControl = () => {
    setIsFocus(!isFocus);
  };

  const classes = useCallback(
    (mainClass) =>
      error && !isFocus ? `${mainClass} ${mainClass}_error` : mainClass,
    [error, isFocus]
  );

  return (
    <FieldContainer>
      {label && <Label>{label}</Label>}
      <FieldContent isError={error && !isFocus}>
        <Component
          name={name}
          placeholder={placeholder}
          handleControl={handleControl}
          register={register}
          isFocus={isFocus}
          validator={validator}
        />
      </FieldContent>
      <p>{error && !isFocus && error.message}</p>
    </FieldContainer>
  );
};

export default Field;
