import React from "react";
import { connect } from "react-redux";

import { newPostPush } from "../../redux/actions/newPost";

import { postNewPostAPI } from "../../api/postNewPostAPI";

import Form, { Field, Input, Textarea } from "../../components/form";
import Button from "../../components/Button";

const validators = {
  title: {
    required: "This field is required",
  },
  text: {
    required: "This field is required",
  },
};

const NewPost: React.FC = (props) => {
  console.log(props);
  const onSubmit = (data) => {
    // postNewPostAPI(data).then((r) => console.log(r));
    props.newPostPush(data);
    console.log(data);
  };
  return (
    <Form onSubmit={onSubmit}>
      {(register, errors) => (
        <>
          <Field
            name="title"
            label="Post title"
            placeholder="enter post title"
            errors={errors}
            component={Input}
            register={register}
            validator={validators.title}
          />
          <Field
            name="body"
            label="text"
            placeholder="enter text"
            errors={errors}
            component={Textarea}
            register={register}
            validator={validators.text}
          />
          <Button type="submit" label="Add" />
        </>
      )}
    </Form>
  );
};

export default connect((state) => state.newPostReducer, { newPostPush })(
  NewPost
);
