import React from "react";
import Link from "next/link";
import styled from "styled-components";

const A = styled.a`
  $text-color: ${props.textColor ? props.textColor : "black"};
  color: $text-color;
  text-decoration: none;

  &:hover {
    color: transparentize($text-color, 0.5);
    cursor: pointer;
  }
`;

type LinkButtonType = {
  text: string;
  textColor?: string | undefined;
  href: string;
};

const LinkButton: React.FC<LinkButtonType> = ({ text, textColor, href }) => {
  return (
    <Link href={href}>
      <A textColor>{text}</A>
    </Link>
  );
};

export default LinkButton;
