import styled from "styled-components";

export const FieldContainer = styled.div`
  width: 100%;
  &:not(last-child) {
    margin-bottom: 20px;
  }
`;

export const Label = styled.p`
  font-size: 18px;
`;

export const FieldContent = styled.div`
  border: 1px solid black;
  border-color: ${(props) => props.isError && "red"};
  margin: 10px 0;
`;
