import React from "react";
import { connect } from "react-redux";

import Post from "../../components/Post";

import { InitialStateType } from "../../redux/reducers/post";

const PostDetails: React.FC<InitialStateType> = ({
  data: { title, body, id, comments },
  error,
}) =>
  error ? (
    <p>ERROR</p>
  ) : (
    <Post title={title} body={body} id={id} comments={comments} />
  );

export default connect((state) => state.postReducer)(PostDetails);
