import { successPost, errorPost } from "../../actions/post";
import { getPostAPI } from "../../../api/getPostAPI";
import { call, put } from "redux-saga/effects";
import { AnyAction } from "redux";

function* postSaga({ payload: { id } }: AnyAction) {
  try {
    const res = yield call(getPostAPI, id);
    // const data = { ...res, comments: res.comments.reverse() };
    yield put(successPost(res));
  } catch (e) {
    yield put(errorPost());
  }
}

export default postSaga;
